import React from 'react';
import OpacityIcon from '@material-ui/icons/Opacity';
import DragIndicatorIcon from '@material-ui/icons/DragIndicator';
import VisibilityIcon from '@material-ui/icons/Visibility';
import SwapVertIcon from '@material-ui/icons/SwapVert';
import SwapHorizIcon from '@material-ui/icons/SwapHoriz';
import FlagIcon from '@material-ui/icons/Flag';
import CloudIcon from '@material-ui/icons/Cloud';
import NearMeIcon from '@material-ui/icons/NearMe';
const Display = (props) => {
    if (main_data.weather[0].main === 'Smoke' || main_data.weather[0].main === 'Mist' || main_data.weather.main === 'Fog' || main_data.weather[0].main === 'Dust'){
        set_weather_attr(()=>{
            return{bg_img:mist_img}
            
            
        })
        
    }
    else if (main_data.weather[0].main === 'Clouds'){set_weather_attr(()=>{
        return{bg_img:cloud_img}
        
    })}
    else if( main_data.weather[0].main === 'Drizzle' || main_data.weather[0].main === 'Rain'){
        set_weather_attr(()=>{
            return{bg_img:rain_img}
            
        })
    }else if( main_data.weather[0].main === 'Thunderstorm'){
        set_weather_attr(()=>{
            return{bg_img:thunder_img}
            
        })
    }else if(  main_data.weather[0].main === 'Snow'){
        set_weather_attr(()=>{
            return{bg_img:snow_img}
            
        })
    }else if(  main_data.weather[0].main === 'Clear'){
        set_weather_attr(()=>{
            return{bg_img:sunny_img}
            
        })
    }else {
        set_weather_attr(()=>{return{bg_img:default_img}})
       

    }
    return(
        <>
         <section >
            <div className="container">
                <div className="row " >
                    <div className="col-2"></div>
                   
                   
                    {!props.main_data?<h1 className="text-center">No data found</h1>:
                    <div className="col-8 border text-center img-fluid " style={{backgroundImage:`url(${""})` ,backgroundSize:`100% 100%`,borderRadius:`15px`}}>
                        <h1 className='mt-5 text-capitalize'>{props.main_data.name},{props.main_data.sys.country}</h1>
                        <h1 >{props.main_data.main.temp}<sup>o</sup>C</h1>
                        <p>Feels like {props.main_data.main.feels_like}<sup>o</sup>C</p>
                        <h2><CloudIcon/> {props.main_data.weather[0].main} </h2>
                        <div className='mb-5'>
                            <span className="mx-2 ">Min Temp:{props.main_data.main.temp_min}<sup>o</sup>C</span><span className="mx-2">Max Temp:{props.main_data.main.temp_max}<sup>o</sup>C</span>   </div> 
                        <div  className='mb-5'>
                            <span className="mx-2 "><i><DragIndicatorIcon/></i>Pressure:{props.main_data.main.pressure} hPa</span><span className="mx-2"><i><OpacityIcon/></i>Humidity:{props.main_data.main.humidity}%</span>    </div>                       
                        <div className='mb-5'> 
                            <span className="mx-2 "><i><VisibilityIcon/></i>Visibility:{props.main_data.visibility/1000} Km</span><span className="mx-2"><i><NearMeIcon/></i>wind:{props.main_data.wind.speed} m/s</span> 
                        </div>
                        <div className='mb-5'> 
                            <span className="mx-2 "><i><SwapVertIcon/></i>longitude:{props.main_data.coord.lon} </span><span className="mx-2"><i><SwapHorizIcon/></i>latitude:{props.main_data.coord.lat}</span> 
                        </div>
                        
                       
                    </div>}
                </div>
            </div>
        </section>
     
        </>
    )
}

export default Display
