import React from 'react';
import OpacityIcon from '@material-ui/icons/Opacity';
import DragIndicatorIcon from '@material-ui/icons/DragIndicator';
import VisibilityIcon from '@material-ui/icons/Visibility';
import SwapVertIcon from '@material-ui/icons/SwapVert';
import SwapHorizIcon from '@material-ui/icons/SwapHoriz';
import FlagIcon from '@material-ui/icons/Flag';
import NearMeIcon from '@material-ui/icons/NearMe';
const Display = (props) => {
    console.log(props)
    return(
        <>
         <section >
            <div className="container">
                <div className="row " >
                    <div className="col-2"></div>
                   
                   
                    {!api_res?<h1 className="text-center">No data found</h1>:
                    <div className="col-8 border text-center img-fluid " style={{backgroundImage:`url(${weather_attr.bg_img})` ,backgroundSize:`100% 100%`,borderRadius:`15px`}}>
                        <h1 className='mt-5 text-capitalize'>{location},{props.api_res.sys.country}</h1>
                        <h1 >{props.api_res.main.temp}<sup>o</sup>C</h1>
                        <p>Feels like {props.api_res.main.feels_like}<sup>o</sup>C</p>
                        <h2><CloudIcon/> {props.api_res.weather[0].main} </h2>
                        <div className='mb-5'>
                            <span className="mx-2 ">Min Temp:{api_res.main.temp_min}<sup>o</sup>C</span><span className="mx-2">Max Temp:{props.api_res.main.temp_max}<sup>o</sup>C</span>   </div> 
                        <div  className='mb-5'>
                            <span className="mx-2 "><i><DragIndicatorIcon/></i>Pressure:{props.api_res.main.pressure} hPa</span><span className="mx-2"><i><OpacityIcon/></i>Humidity:{props.api_res.main.humidity}%</span>    </div>                       
                        <div className='mb-5'> 
                            <span className="mx-2 "><i><VisibilityIcon/></i>Visibility:{props.api_res.visibility/1000} Km</span><span className="mx-2"><i><NearMeIcon/></i>wind:{props.api_res.wind.speed} m/s</span> 
                        </div>
                        <div className='mb-5'> 
                            <span className="mx-2 "><i><SwapVertIcon/></i>longitude:{props.api_res.coord.lon} </span><span className="mx-2"><i><SwapHorizIcon/></i>latitude:{props.api_res.coord.lat}</span> 
                        </div>
                        
                       
                    </div>}
                </div>
            </div>
        </section>
     
        </>
    )
}

export default Display
