import React,{useState,useEffect} from 'react';
import axios from 'axios';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import '../../node_modules/bootstrap/dist/js/bootstrap.bundle';
import CloudIcon from '@material-ui/icons/Cloud';
import '../css/app.css';
import cloud_img from '../images/cloud.gif'
import FlagIcon from '@material-ui/icons/Flag';
import NearMeIcon from '@material-ui/icons/NearMe';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import OpacityIcon from '@material-ui/icons/Opacity';
import DragIndicatorIcon from '@material-ui/icons/DragIndicator';
import VisibilityIcon from '@material-ui/icons/Visibility';
import SwapVertIcon from '@material-ui/icons/SwapVert';
import SwapHorizIcon from '@material-ui/icons/SwapHoriz';

const Temp_app = ()=>{
    const [location,setLocation] =useState('Delhi')
    const [api_res,set_api_res] =useState({})
    const key = '7fa4fc93e21f5c2987bd51291b001272'

    async function get_data(){ 
            
        const res = await axios.get(`http://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=${key}`);
        set_api_res(()=>{
            return(res.data)
        })
    
    };

    const null_valuse = ()=>{
        return(
        (!location)?set_api_res({}) :null)
        
    }

    useEffect(()=>{
        get_data();
        null_valuse();
        console.log(api_res)
            
           
    },[location])

    return (
        <>
       
       
        <header>
        <section  >
            <div className="container-fluid">
                <div className="row">
                    <h1 className=" col-lg-12 col-md-12 col-sm-12 text-center text-capitalize my-5 text-dark font-weight-bolder">Weather App</h1>
                </div>

            </div>
        </section>
        </header>

        <section className='container-fluid '>
            <article>
                <div className='row'>
                <div className='col-lg-4 col-sm-11 col-md-4 text-right  float-left'>
                <i className='object mb-3' ><LocationOnIcon/></i>
                </div>
                <div className=" col-lg-4 col-md-4 col-sm-12 ">
                
                    <div className=" form-group ">
                    
                    <input className=" text-center form-control text-center" type="search" value={location} onChange={(e)=>{setLocation(e.target.value)}} placeholder="Your Location"/>
                    </div>
                </div>
                </div>
            </article>

        </section>

        <section >
            <div className="container">
                <div className="row " >
                    <div className="col-2"></div>
                    <div className="col-8 border text-center img-fluid " style={{backgroundImage:`url(${cloud_img})` ,backgroundSize:`100% 100%`,borderRadius:`15px`}}>
                        <h1 className='mt-5'>{location},{api_res.sys.country}</h1>
                        <h1 >{api_res.main.temp}<sup>o</sup>C</h1>
                        <h2><CloudIcon/></h2>
                        <div className='mb-5'>
                        <span className="mx-2 ">Min Temp:10<sup>o</sup>C</span><span className="mx-2">Max Temp:30<sup>o</sup>C</span>   </div> 
                        <div  className='mb-5'>

                        <span className="mx-2 "><i><DragIndicatorIcon/></i>Pressure:10 Hg</span><span className="mx-2"><i><OpacityIcon/></i>Humidity:30 %</span>    </div>
                           

                        <div className='mb-5'> 
                        <span className="mx-2 "><i><VisibilityIcon/></i>Visibility:10 cm</span><span className="mx-2"><i><NearMeIcon/></i>wind:30 %</span> 
                            
                        </div>
                        <div className='mb-5'> 
                        <span className="mx-2 "><i><SwapVertIcon/></i>longitude:10 cm</span><span className="mx-2"><i><SwapHorizIcon/></i>latitude:30 %</span> 
                            
                        </div>
                        
                       
                    </div>
                </div>
            </div>
        </section>
     
        </>
    )

}

export default Temp_app
