import React,{useState,useEffect} from 'react';
import axios from 'axios';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
// import '../../node_modules/bootstrap/dist/js/bootstrap.bundle';
import CloudIcon from '@material-ui/icons/Cloud';
import '../css/app.css';
import cloud_img from '../images/cloud.gif'
import sunny_img from '../images/sunny.gif'
import rain_img from '../images/rain.gif'
import snow_img from '../images/snow.gif'
import thunder_img from '../images/thunder.gif'
import mist_img from '../images/mist.gif'
import bg_img from '../images/main_bg.jpg'
import FlagIcon from '@material-ui/icons/Flag';
import NearMeIcon from '@material-ui/icons/NearMe';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import OpacityIcon from '@material-ui/icons/Opacity';
import DragIndicatorIcon from '@material-ui/icons/DragIndicator';
import VisibilityIcon from '@material-ui/icons/Visibility';
import SwapVertIcon from '@material-ui/icons/SwapVert';
import SwapHorizIcon from '@material-ui/icons/SwapHoriz';


const Temp_app = ()=>{
    const [location,setLocation] =useState('Delhi')
    const [api_res,set_api_res] =useState()
    const [weather_attr,set_weather_attr] =useState({bg_img:'',icon_:''})
    const key = '7fa4fc93e21f5c2987bd51291b001272'

    async function get_data(){ 
            
        const res = await axios.get(`http://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=${key}`).then((response)=>{console.log(response);set_api_res(response.data)}).catch((response)=>{set_api_res(null)});
        // set_api_res(()=>{
        //     return(res.data)
        // })
    
    };

   

    useEffect(()=>{
        console.log("__________------------------")
        get_data();
       
        console.log(location)
       
           
    },[location])

    const change_img = ()=>{
        try {
            console.log("This is the culprit*******888",api_res.weather[0].main)
        } catch (error) {
            return(null)
        }
        
        if (api_res.weather[0].main === 'Smoke' || api_res.weather[0].main === 'Mist' || api_res.weather.main === 'Fog' || api_res.weather[0].main === 'Dust'){
            set_weather_attr(()=>{
                return{bg_img:mist_img}
                
            })

        }
        else if (api_res.weather[0].main === 'Clouds'){set_weather_attr(()=>{
            return{bg_img:cloud_img}
            
        })}
        else if( api_res.weather[0].main === 'Drizzle' || api_res.weather[0].main === 'Rain'){
            set_weather_attr(()=>{
                return{bg_img:rain_img}
                
            })
        }else if( api_res.weather[0].main === 'Thunderstorm'){
            set_weather_attr(()=>{
                return{bg_img:thunder_img}
                
            })
        }else if(  api_res.weather[0].main === 'Snow'){
            set_weather_attr(()=>{
                return{bg_img:snow_img}
                
            })
        }else if(  api_res.weather[0].main === 'Clear'){
            set_weather_attr(()=>{
                return{bg_img:sunny_img}
                
            })
        }
    }

    const change_happen = (e)=>{
        setLocation(e.target.value)
        console.log('@@@@@@@@@@@@22',api_res.weather[0].main )
        change_img()
    }

    return (
        <>
        
       {console.log("...........................",api_res.weather[0].main )}
       <img src={bg_img} style={{position:'absolute',zIndex:'-1',width:'100%',height:'100vh'}} alt="bg-img"/>
        <header>
        <section  >
            <div className="container-fluid">
                <div className="row">
                    <h1 className=" col-lg-12 col-md-12 col-sm-12 text-center text-capitalize my-5 text-dark font-weight-bolder">Weather App</h1>
                </div>

            </div>
        </section>
        </header>

        <section className='container-fluid '>
            <article>
                <div className='row'>
                <div className='col-lg-4 col-sm-11 col-md-4 text-right  float-left'>
                <i className='object mb-3' ><LocationOnIcon/></i>
                </div>
                <div className=" col-lg-4 col-md-4 col-sm-12 ">
                
                    <div className=" form-group ">
                    {change_img}
                    <input className=" text-center form-control text-center" type="search" value={location} onChange={change_happen} placeholder="Your Location"/>
                    </div>
                </div>
                </div>
            </article>

        </section>

        <section >
            <div className="container">
                <div className="row " >
                    <div className="col-2"></div>
                   
                   
                    {!api_res?<h1 className="text-center">No data found</h1>:
                    <div className="col-8 border text-center img-fluid " style={{backgroundImage:`url(${weather_attr.bg_img})` ,backgroundSize:`100% 100%`,borderRadius:`15px`}}>
                        <h1 className='mt-5 text-capitalize'>{location},{api_res.sys.country}</h1>
                        <h1 >{api_res.main.temp}<sup>o</sup>C</h1>
                        <p>Feels like {api_res.main.feels_like}<sup>o</sup>C</p>
                        <h2><CloudIcon/> {api_res.weather[0].main} </h2>
                        <div className='mb-5'>
                            <span className="mx-2 ">Min Temp:{api_res.main.temp_min}<sup>o</sup>C</span><span className="mx-2">Max Temp:{api_res.main.temp_max}<sup>o</sup>C</span>   </div> 
                        <div  className='mb-5'>
                            <span className="mx-2 "><i><DragIndicatorIcon/></i>Pressure:{api_res.main.pressure} hPa</span><span className="mx-2"><i><OpacityIcon/></i>Humidity:{api_res.main.humidity}%</span>    </div>                       
                        <div className='mb-5'> 
                            <span className="mx-2 "><i><VisibilityIcon/></i>Visibility:{api_res.visibility/1000} Km</span><span className="mx-2"><i><NearMeIcon/></i>wind:{api_res.wind.speed} m/s</span> 
                        </div>
                        <div className='mb-5'> 
                            <span className="mx-2 "><i><SwapVertIcon/></i>longitude:{api_res.coord.lon} </span><span className="mx-2"><i><SwapHorizIcon/></i>latitude:{api_res.coord.lat}</span> 
                        </div>
                        
                       
                    </div>}
                </div>
            </div>
        </section>
     
        </>
    )

}

export default Temp_app
